mapper.select:
SELECT (
    EXISTS (
        SELECT
            1
        FROM
            Account
        WHERE
            mailAddress = #{mailAddress}
    )
)

mapper.insert:
INSERT INTO Account(
    mailAddress,
    password
) VALUES (
    #{mailAddress},
    #{password}
)

mapper.update:
UPDATE Account
SET
    nickname = #{nickname}
WHERE
    accountId = #{accountId}
