mapper.select:
SELECT (
    EXISTS (
        SELECT
            1
        FROM
            Plan
        WHERE
            planId = #{planId}
    )
)

SELECT (
    EXISTS (
        SELECT
            1
        FROM
            Plan P
            INNER JOIN Room R ON P.roomId = R.roomId
            INNER JOIN AccountJoinRoom AJR ON R.roomId = AJR.roomId AND AJR.accountId = #{accountId}
        WHERE
            P.planId = #{planId}
    )
)
